function initMap(){map=new google.maps.Map(document.getElementById("map"),{center:coords,zoom:13,disableDefaultUI:!0}),$("#submit-city").attr("id","submit-newCity");var o=new google.maps.Geocoder;document.getElementById("submit-newCity").addEventListener("click",function(){geocodeNewCity(o,map)})}function geocodeNewCity(o,t){var e=$("#city").val();o.geocode({address:e},function(o,e){e===google.maps.GeocoderStatus.OK?t.setCenter(o[0].geometry.location):alert("Geocode was not successful for the following reason: "+e)})}function getGeoLocation(){navigator.geolocation&&navigator.geolocation.getCurrentPosition(function(o){coords.lat=o.coords.latitude,coords.lng=o.coords.longitude,initMap()})}function getLocationManually(){var o="https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyAZKV6_aryLoI5q40ikKRJ_Qy-32Hg-3ng&address=",t=$("#city").val(),e=o+t;console.log(t),console.log(e),$.getJSON(e,function(o){pos=o.results[0].geometry.location,coords.lat=pos.lat,coords.lng=pos.lng,initMap()})}var map,coords={};getGeoLocation(),$("#getCity").submit(getLocationManually);